mapper.select:
SELECT anino, uid, aname, age, gender, weight, genop, vac, intro, anipic, adopt, cnt, adopt_pos
FROM animal
WHERE anino = #{anino}

SELECT anipic
FROM animal
WHERE anino = #{anino}

mapper.update:
UPDATE animal 
SET anino = #{anino}, uid = 'kim9595', aname = #{aname}, age = #{age}, gender = #{gender}, weight = #{weight}, genop = #{genop}, vac = #{vac}, intro = #{intro}, anipic = #{anipic}, adopt = #{adopt}, adopt_pos = #{adopt_pos}
WHERE anino = #{anino}
