mapper.select:
SELECT textno, texttitle, uploaddate, cnt, uid
FROM help
WHERE uid = #{uid}

SELECT arno, artitle, ardate, cnt, uid
FROM adoprv
WHERE uid = #{uid}

SELECT sno, stitle, stitle, cnt, uid
FROM service
WHERE uid = #{uid}

SELECT COUNT(*)
FROM help 
WHERE uid = #{uid};

SELECT COUNT(*)
FROM adoprv
WHERE uid = #{uid};

SELECT COUNT(*)
FROM adopcm
WHERE uid = #{uid};

  SELECT AA.apno, AA.anipic, AA.sub_cont, AA.sub_state, AA.subpic, py.payno, py.payop, py.pdate, py.paymet, py.patamt, AA.sub_date, AA.anino
	FROM payment py
	JOIN (
		    SELECT ap.apno, an.anipic, ap.sub_cont, ap.sub_state, ap.subpic, ap.sub_date, ap.anino
		    FROM adopt ap
		    JOIN animal an ON ap.anino = an.anino
		    WHERE ap.uid = #{uid}
	     ) AA ON py.apno = AA.apno
	WHERE py.uid = #{uid}

 	SELECT COUNT(*)
	FROM member
	WHERE passwd = #{passwd}

mapper.update:
UPDATE adopt
SET sub_state = #{sub_state}
WHERE uid = #{uid}

UPDATE member
SET mlevel = #{mlevel}
WHERE uid = #{uid}

UPDATE member
SET mlevel = 'D'
WHERE uid = #{uid}

UPDATE s_member
SET s_mlevel = 'D'
WHERE s_email = #{uid}

mapper.select:
SELECT uname, uid, email, passwd, tel, birth, zipcode, addr1, addr2
FROM member 
WHERE uid = #{uid}

mapper.update:
UPDATE member
SET passwd = #{passwd}, tel = #{tel}, birth = #{birth}, zipcode = #{zipcode}, addr1 = #{addr1}, addr2 = #{addr2} 
WHERE uid = #{uid}

UPDATE payment
SET payop = 'Y', pdate = NOW(), patamt = '50000'
WHERE uid = #{uid}

UPDATE payment
SET payop = 'H'
WHERE uid = #{uid}

UPDATE adopt
SET sub_state = 'F'
WHERE uid = #{uid}
