mapper.update:
UPDATE service
SET cnt = cnt + 1
WHERE sno = #{sno}

mapper.select:
SELECT sno, uid, stitle, sinfo, ssdate, sedate, sstime, etime, time, sp, splace, sstate, cnt
FROM service
WHERE sno = #{sno}

mapper.update:
UPDATE service 
SET sno = #{sno}, uid = #{uid}, stitle = #{stitle}, sinfo = #{sinfo}, ssdate = #{ssdate}, sedate = #{sedate}, sstime = #{sstime}, etime = #{etime}, time = #{time}, sp = #{sp}, splace = #{splace}, sstate = #{sstate}
WHERE sno = #{sno}
