mapper.select:
SELECT sno, uid, stitle, sinfo, ssdate, sedate, time, sp, splace, sstate, cnt
FROM service
WHERE sno = #{sno}

mapper.update:
UPDATE service 
SET sno = #{sno}, uid = 'kim9595', stitle = #{stitle}, sinfo = #{sinfo}, ssdate = #{ssdate}, sedate = #{sedate}, time = #{time}, sp = #{sp}, splace = #{splace}, sstate = #{sstate}
WHERE sno = #{sno}
